import React, {useState, useEffect} from 'react';
import RockPaperScissors from './contracts/RockPaperScissors.json';
import  Web3 from 'web3';
// import { getWeb3 } from './utils.js';

function App() {
  const [web3, setWeb3] = useState(undefined);
  const [contract, setContract] = useState(undefined);
  const [testValue, setTestValue] = useState(undefined);
  const [choice, setChoice] = useState(undefined);


  useEffect(() => {
    const init = async () => {
      const web3 = await new Web3("http://localhost:9545");

      const networkId = await web3.eth.net.getId();
      const deployedNetwork = RockPaperScissors.networks[networkId];
      const contract = new web3.eth.Contract(
          RockPaperScissors.abi,
          deployedNetwork && deployedNetwork.address,
      );
      setWeb3(web3);
      setContract(contract);
    }
    init();
  }, []);

  // async function makeChoice(e){
  //   e.preventDefault();
  //   const re = await contract.methods.test2().call();
  //   setTestValue(re);
  //   console.log(re);
  //   console.log(testValue);
  // }

  async function rock(e){
    await contract.methods.makeChoice("Rock").call();
    e.preventDefault();
    const re = await contract.methods.test2().call();
    setTestValue(re);
    console.log(re);
    console.log(testValue);
  }


  async function test2(e){
    e.preventDefault();
    const re = await contract.methods.test2().call();
    setTestValue(re);
    console.log(re);
    console.log(testValue);
  }

  if (!web3) {
    return <div>Loading...</div>;
  }

  return (
    <div className="App">
      <div className="desk">
        <div className="part">Player1</div>
        <div className="part">Result</div>
        <div className="part">Player2</div>
      </div>
      <h2>Moves:</h2>
      <div>
        <div onClick = {e => rock(e)} >Rock</div>
        <div>paper</div>
        <div>Scissor</div>
        <div>Current Choice:</div>
      </div>



      <form onSubmit={e => test2(e)}>
        <div>
          <label htmlFor="string-length">Bet Amount</label>
          <input type="text" />
        </div>
        <button type="submit">Submit</button>
        <button type="submit">Reveal Result</button>
        <p>{testValue && `Result: ${testValue}`}</p>
      </form>
      <div> <button type="submit">New Game</button></div>

    </div>
  );
}

export default App;
